import { motion } from 'framer-motion';
import { useState } from 'react';
import useStore from '../store/useStore.jsx';
import ReactPlayer from 'react-player';
import EditEventModal from './EditEventModal.jsx';

const Timeline = () => {
  const events = useStore((state) => state.events);
  const selectedEvent = useStore((state) => state.selectedEvent);
  const setSelectedEvent = useStore((state) => state.setSelectedEvent);
  const removeEvent = useStore((state) => state.removeEvent);
  const updateEvent = useStore((state) => state.updateEvent);
  const [editingEvent, setEditingEvent] = useState(null);
  const [hoveredEvent, setHoveredEvent] = useState(null);

  // Сортируем события по году в порядке убывания (поздние годы слева)
  const sortedEvents = [...events].sort((a, b) => b.year - a.year);

  const handleEdit = (event) => {
    setEditingEvent(event);
  };

  const handleDelete = (eventId) => {
    if (window.confirm('Вы уверены, что хотите удалить это событие?')) {
      removeEvent(eventId);
      if (selectedEvent?.id === eventId) {
        setSelectedEvent(null);
      }
    }
  };

  const handleSaveEdit = (updatedEvent) => {
    updateEvent(updatedEvent);
    if (selectedEvent?.id === updatedEvent.id) {
      setSelectedEvent(updatedEvent);
    }
  };

  const handleEventClick = (event) => {
    if (selectedEvent?.id === event.id) {
      setSelectedEvent(null);
    } else {
      setSelectedEvent(event);
    }
  };

  return (
    <div className="relative">
      <div className="flex overflow-x-auto pb-8">
        <div className="flex space-x-8">
          {sortedEvents.map((event) => (
            <motion.div
              key={event.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              className="flex-shrink-0 w-64"
              onMouseEnter={() => setHoveredEvent(event)}
              onMouseLeave={() => setHoveredEvent(null)}
            >
              <div
                className={`p-4 rounded-lg cursor-pointer relative ${
                  selectedEvent?.id === event.id
                    ? 'bg-blue-500 text-white'
                    : 'bg-white hover:bg-gray-100'
                }`}
                onClick={() => handleEventClick(event)}
              >
                <h3 className="font-bold text-lg">{event.title}</h3>
                <p className="text-sm">{event.year}</p>
                <div className="mt-2 flex space-x-2">
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleEdit(event);
                    }}
                    className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded-md text-sm font-medium transition-colors duration-200"
                  >
                    Редактировать
                  </button>
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDelete(event.id);
                    }}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-md text-sm font-medium transition-colors duration-200"
                  >
                    Удалить
                  </button>
                </div>
                {hoveredEvent?.id === event.id && (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: 10 }}
                    className="absolute top-full left-0 mt-2 w-full bg-white rounded-lg shadow-lg overflow-hidden z-10 border border-gray-200"
                  >
                    <div className="w-full h-[80px] relative">
                      <div className="absolute inset-0 bg-gradient-to-r from-gray-900 to-transparent opacity-50" />
                      <div className="absolute inset-0 flex items-center px-4">
                        <div className="w-12 h-12 bg-gray-800 rounded-md flex items-center justify-center mr-3">
                          <svg
                            className="w-6 h-6 text-white"
                            fill="none"
                            stroke="currentColor"
                            viewBox="0 0 24 24"
                          >
                            <path
                              strokeLinecap="round"
                              strokeLinejoin="round"
                              strokeWidth={2}
                              d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z"
                            />
                            <path
                              strokeLinecap="round"
                              strokeLinejoin="round"
                              strokeWidth={2}
                              d="M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                            />
                          </svg>
                        </div>
                        <div className="text-white">
                          <p className="font-medium text-sm truncate">{event.title}</p>
                          <p className="text-xs text-gray-300">{event.year}</p>
                        </div>
                      </div>
                      <ReactPlayer
                        url={event.songUrl}
                        width="100%"
                        height="100%"
                        playing={false}
                        controls={true}
                        light={true}
                        style={{ position: 'absolute', top: 0, left: 0 }}
                        className="opacity-0"
                      />
                    </div>
                  </motion.div>
                )}
              </div>
            </motion.div>
          ))}
        </div>
      </div>

      {selectedEvent && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="mt-8 p-6 bg-white rounded-lg shadow-md"
        >
          <h2 className="text-2xl font-bold mb-4">{selectedEvent.title}</h2>
          <p className="text-gray-600 mb-4">{selectedEvent.description}</p>
          <div className="aspect-video">
            <ReactPlayer
              url={selectedEvent.songUrl}
              width="100%"
              height="100%"
              controls
            />
          </div>
        </motion.div>
      )}

      {editingEvent && (
        <EditEventModal
          event={editingEvent}
          onClose={() => setEditingEvent(null)}
          onSave={handleSaveEdit}
        />
      )}
    </div>
  );
};

export default Timeline;
