import { useState, useEffect } from 'react';
import AddEventForm from './components/AddEventForm.jsx';
import Timeline from './components/Timeline.jsx';
import ThemeToggle from './components/ThemeToggle.jsx';

function App() {
  const [showSaveMessage, setShowSaveMessage] = useState(false);

  useEffect(() => {
    const handleBeforeUnload = (e) => {
      e.preventDefault();
      e.returnValue = '';
      setShowSaveMessage(true);
    };

    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => window.removeEventListener('beforeunload', handleBeforeUnload);
  }, []);

  return (
    <div className="min-h-screen p-8 bg-gradient-to-br from-indigo-50 to-blue-100 dark:from-neutral-900 dark:to-gray-950 transition-colors duration-300">
      <div className="max-w-6xl mx-auto">
        <div className="text-center mb-12">
          <h1 className="text-5xl font-bold text-gray-800 dark:text-white mb-4">
            Саундтрек моей жизни
          </h1>
          <p className="text-xl text-gray-600 dark:text-gray-400">
            Музыкальное путешествие по вашим воспоминаниям
          </p>
        </div>

        {showSaveMessage && (
          <div className="bg-yellow-100 dark:bg-yellow-900 border-l-4 border-yellow-500 text-yellow-700 dark:text-yellow-200 p-4 mb-4 rounded-lg">
            <p>Не забудьте сохранить изменения перед перезагрузкой страницы!</p>
          </div>
        )}

        <div className="space-y-8">
          <AddEventForm />
          <Timeline />
        </div>
      </div>
      <ThemeToggle />
    </div>
  );
}

export default App;
